mapper.update:
UPDATE NOTICE_BOARD
SET
title = #{title},
bcontents = #{bcontents}
WHERE bno = #{bno}

UPDATE NOTICE_BOARD
SET BCOUNTS = BCOUNTS + 1
WHERE BNO = #{bno}

UPDATE POINT 
SET 
PCOUNT = PCOUNT + #{pcount}
, PSOURCE = #{psource}
, PDATE = SYSDATE
WHERE MNO = #{mno}
